<?php\nif ($_SERVER['REQUEST_METHOD'] === 'POST') {\n    if (isset($_FILES['scannedImage']) && $_FILES['scannedImage']['error'] === UPLOAD_ERR_OK) {\n        $tmp_name = $_FILES['scannedImage']['tmp_name'];\n        $name = basename($_FILES['scannedImage']['name']);\n\n        // Define a safe location to store the uploaded image\n        $upload_dir = 'uploads/';\n        if (!file_exists($upload_dir)) {\n            mkdir($upload_dir, 0755, true);\n        }\n\n        // Move the uploaded file to its destination\n        $destination = $upload_dir . basename($name);\n        if (move_uploaded_file($tmp_name, $destination)) {\n            echo "Image successfully uploaded to: " . htmlspecialchars($destination);\n        } else {\n            echo "Failed to move uploaded image.";\n        }\n    } else {\n        echo "Error uploading file. Error code: " . $_FILES['scannedImage']['error'];\n    }\n} else {\n    echo "Only POST requests are allowed.";\n}\n?>