for($k=0;$k<=$x->length-1;$k++) {\n    for($l=0;$l<=$j-1;$l++) {\n        if($y->item($k)->nodeValue == $JobNoArr[$l]) {\n            $m++;\n            $bgColor = ($m % 2 == 0) ? "#A5ADEA" : "#D1D1D3";\n\n            echo "<tr bgcolor='" . htmlspecialchars($bgColor, ENT_QUOTES, 'UTF-8') . "'>";\n            echo "<td>" . htmlspecialchars($m, ENT_QUOTES, 'UTF-8') . "</td>";\n            echo "<td>" . htmlspecialchars($y->item($k)->nodeValue, ENT_QUOTES, 'UTF-8') . "</td>";\n            echo "<td>" . htmlspecialchars($TitleArr[$l], ENT_QUOTES, 'UTF-8') . "</td>";\n            // Pass the node value to JavaScript function\n            echo "<td onclick='show(" . intval($u->item($k)->nodeValue) . ")'><i><font size=2>Click for Abstract</font></i></td>";\n            echo "<td>" . htmlspecialchars($uu, ENT_QUOTES, 'UTF-8') . "</td>";\n            echo "</tr>";\n        }\n    }\n}