<?php\nrequire_once "../../includes/constants.php";\n\n// Create connection\n$conn = new mysqli(DB_SERVER, DB_USER, DB_PASSWORD, DB_NAME);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\necho "<table><tbody>";\n$sql = "SELECT * FROM place";\n$result = $conn->query($sql);\n\nif ($result->num_rows > 0) {\n    while($row = $result->fetch_assoc()) {\n        echo "<tr><td id='" . $row["id"] . "' class='inputfield_td'><input class='inputfield_place' type='text' value='" . htmlspecialchars($row["name"]) . "' /></td>";\n        echo "<td class='place_name'>" . htmlspecialchars($row["name"]) . "</td>";\n        echo "<td class='edit'>edit</td><td class='cancel'>cancel</td><td class='delete'>delete</td><td class='save'>SAVE</td></tr> \n";\n    }\n} else {\n    echo "0 results";\n}\necho "</tbody></table>";\necho "<input type='text' class='inputfield_visible' />";\necho "<button class='new'>New</button>";\n\n$conn->close();\n?>