function getLeadingString($string) {\n    $posSpace = strpos($string, ' ');\n    $posDot = strpos($string, '.');\n\n    // If neither delimiter is found, return the whole string\n    if ($posSpace === false && $posDot === false) {\n        return $string;\n    }\n\n    // Determine which position is valid and smaller\n    $positions = array_filter([$posSpace, $posDot], function($p) { return $p !== false; });\n    $posMin = min($positions);\n\n    return substr($string, 0, $posMin);\n}\n\n$string1 = "test.test";\n$result1 = getLeadingString($string1); // result = test\n\n$string2 = "test doe";\n$result2 = getLeadingString($string2); // result = test\n\necho "Result 1: $result1\n"; // Outputs: Result 1: test\necho "Result 2: $result2\n"; // Outputs: Result 2: test