$userId = 1; // Example user ID\n$query = "SELECT item_id FROM user_items WHERE user_id = ?";\n$stmt = $pdo->prepare($query);\n$stmt->execute([$userId]);\n$itemsCollected = $stmt->fetchAll(PDO::FETCH_COLUMN);\n\nforeach ($itemsCollected as $itemId) {\n    $itemNameQuery = "SELECT item_name FROM items WHERE item_id = ?";\n    $stmtItemName = $pdo->prepare($itemNameQuery);\n    $stmtItemName->execute([$itemId]);\n    $itemName = $stmtItemName->fetchColumn();\n    $imageName = $itemName . ".jpg";\n    echo '<img src="images/' . htmlspecialchars($imageName) . '" alt="' . htmlspecialchars($itemName) . '">';\n}