function processYouTubeVideos($str) {\n    // Use preg_match to find the video ID in the URL\n    if (preg_match('/v=([^&]+)/', $str, $matches)) {\n        $videoId = htmlspecialchars($matches[1]);\n\n        // Return the HTML object with the video ID embedded\n        return '<object width="400" height="245">' .\n               '<param name="movie" value="http://www.youtube-nocookie.com/v/' . $videoId . '&hl=en&fs=1&rel=0&showinfo=0"></param>' .\n               '<param name="allowFullScreen" value="true"></param><param name="allowscriptaccess" value="always"></param>' .\n               '<embed src="http://www.youtube-nocookie.com/v/' . $videoId . '&hl=en&fs=1&rel=0&showinfo=0" type="application/x-shockwave-flash" allowscriptaccess="always" allowfullscreen="true" width="400" height="245"></embed>' .\n               '</object>';\n    }\n    return ''; // Return an empty string if no match is found\n}\n\n// Use preg_replace instead of eregi_replace and call the function properly\n$str = preg_replace_callback('/<youtube=([^>]*)>/', function($matches) {\n    return processYouTubeVideos($matches[1]);\n}, $str);