<?php\n\nnamespace App\Controller;\n\nuse Symfony\Bundle\FrameworkBundle\Controller\AbstractController;\nuse Symfony\Component\HttpFoundation\Response;\nuse App\Utility\StringHelper; // Assuming StringHelper is in your C:\Development directory\n\nclass DefaultController extends AbstractController {\n    public function indexAction() {\n        $string = 'example';\n        $modifiedString = StringHelper::concatenate($string, ' modification');\n        return new Response('Modified string: ' . $modifiedString);\n    }\n}