<?php\n    $url = "http://www.legrandjournal.com.mx/category/actu-monde/feed/";\n\n    $ch = curl_init();\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n    curl_setopt($ch, CURLOPT_URL, $url);\n    curl_setopt($ch, CURLOPT_TIMEOUT, 10); // Set a timeout\n    curl_setopt($ch, CURLOPT_USERAGENT, 'Mozilla/5.0 (compatible; PHP; Curl)'); // Add user-agent\n    //curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false); // Uncomment if using HTTPS and having issues\n\n    $awel = curl_exec($ch);\n\n    if ($awel === false) {\n        echo 'Curl error: ' . curl_error($ch);\n    } else {\n        echo $awel;\n    }\n\n    curl_close($ch);\n?>