<?php\n\n$url = "http://apple.accuweather.com/adcbin/apple/Apple_Weather_Data.asp?zipcode=EUR;PT;PO019;REGUA";\n\n$ch = curl_init();\ncurl_setopt($ch, CURLOPT_URL, $url);\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\n// Set a longer timeout period if necessary\ncurl_setopt($ch, CURLOPT_CONNECTTIMEOUT, 30); // 30 seconds\ncurl_setopt($ch, CURLOPT_TIMEOUT, 60); // 60 seconds total\n\n$xmlString = curl_exec($ch);\nif(curl_errno($ch)) {\n    echo 'Request Error:' . curl_error($ch);\n} else {\n    $xmlobj = simplexml_load_string($xmlString);\n    header("Content-type: text/plain");\n    print_r($xmlobj);\n}\n\ncurl_close($ch);\n\n?>