// Open the file with BOM detection for UTF-16\n$filehandle = fopen($tmpfile, 'r');\nstream_filter_append($filehandle, 'convert.iconv.utf-16le/utf-8');\n\nwhile (($data = fgetcsv($filehandle, 1000, "\t")) !== FALSE) { // Use tab as a delimiter for TSV\n    $values[] = array(\n        'id' => (int)$data[0], \n        'type' => trim($data[1]), \n        'text' => trim($data[4]), \n        'desc' => trim($data[5]), \n        'pdf' => trim($data[7])\n    );\n}\nfclose($filehandle);