// Initialize the output array\n$output = array(\n    'Inbox' => array('id' => array()),\n    'Outbox' => array('id' => array())\n);\n\n// Query the database for inbox and outbox messages\n$inbox = $db->Query("SELECT * FROM mail_inbox");\n$outbox = $db->Query("SELECT * FROM mail_outbox");\n\n// Populate the Inbox with message sequence ids\nforeach($inbox as $array) {\n    $output['Inbox']['id'][] = $array['msg_seq'];\n}\n\n// Populate the Outbox with message sequence ids\nforeach($outbox as $array) {\n    $output['Outbox']['id'][] = $array['msg_seq'];\n}\n\n// Print the resulting array for debugging purposes\nprint_r($output);