<?php\n\n$filename = '/tmp/script_' . time() . '.tmp';\n\n// Open the file in your preferred editor (vi in this case)\n$descriptorspec = array(\n    0 => STDIN,  // stdin is a pipe that the child will read from\n    1 => STDOUT, // stdout is a pipe that the child will write to\n    2 => STDERR  // stderr is a pipe that the child will write to\n);\n\n$process = proc_open('vi ' . escapeshellarg($filename), $descriptorspec, $pipes);\n\nif (is_resource($process)) {\n    // Wait for the editor to close\n    proc_close($process);\n\n    // Read input from the file created by the editor\n    if (file_exists($filename)) {\n        $input = file_get_contents($filename);\n        echo "You entered:\n";\n        echo $input;\n\n        // Clean up the temporary file after reading\n        unlink($filename);\n    } else {\n        echo "The file does not exist.\n";\n    }\n} else {\n    echo "Failed to open the editor.\n";\n}\n?>