function limitString($string, $limit = 25) {\n    if (strlen($string) > $limit) {\n        return substr($string, 0, $limit - 3) . '...';\n    } else {\n        return $string;\n    }\n}\n\n// Example usage:\n$originalString = '12345678901234567890abcdefg';\n$limitedString = limitString($originalString);\necho $limitedString; // Outputs: 12345678901234567890abcde...