function unsignedToSigned($unsignedInt) {\n    // Check if the number is greater than the maximum for a signed 32-bit integer\n    if ($unsignedInt >= 0x80000000) {\n        // Convert it to a negative value by subtracting it from the 32-bit max + 1\n        return $unsignedInt - 0x100000000;\n    }\n    return $unsignedInt;\n}\n\n// Example usage:\n$ip = ip2long('192.168.1.1');\n$signedIp = unsignedToSigned($ip);\necho $signedIp; // Outputs the signed integer representatio