<?php\n$data = "plaintext data";\n$pass = openssl_random_pseudo_bytes(32); // Ensure 256-bit key\n\n$options = OPENSSL_RAW_DATA;\n$ivlen = openssl_cipher_iv_length($cipher="aes-256-cbc");\n$iv = openssl_random_pseudo_bytes($ivlen);\n\n$cdata = openssl_encrypt($data, $cipher, $pass, $options, $iv);\necho base64_encode($cdata) . "\n";\n\n// Note: To decrypt you need both the IV and the same key\n?>