<?php\n\n$originalArray = array(\n    'key1' => 'value1',\n    'key2' => 'value2',\n    // ... other elements ...\n    'key57' => 'value57'\n);\n\n$keysToKeep = ['key2', 'key57'];\n\n// Use array_flip to create an associative array with the keys as values\n$keysToKeepFlipped = array_flip($keysToKeep);\n\n// Use array_intersect_key to keep only the desired keys\n$resultArray = array_intersect_key($originalArray, $keysToKeepFlipped);\n\nprint_r($resultArray);\n?>