<?php\n// Sample datetime strings from your database\n$datetime1 = '2009-07-26 18:42:21';\n$datetime2 = '2009-07-26 19:45:32';\n\n// Create DateTime objects\n$date1 = new DateTime($datetime1);\n$date2 = new DateTime($datetime2);\n\n// Calculate the difference between the two dates\n$interval = $date1->diff($date2);\n\n// Get the total seconds from the interval\n$totalSeconds = $interval->s + ($interval->i * 60) + ($interval->h * 3600) + ($interval->days * 86400);\n\necho "The time difference is $totalSeconds seconds.";\n?>