<?php\nfunction handleError($errno, $errstr) {\n    // Log error (optional)\n    error_log("Error: [$errno] $errstr");\n\n    // Set header for 500 Internal Server Error and redirect to custom error page\n    http_response_code(500);\n    header("Location: /error_page.php?code=500");\n    exit;\n}\n\nset_error_handler("handleError");\n\n// Example trigger\n$undefinedVar; // This will invoke the handleError function\n?>