<?php\n\nfunction syncTableStructures($sourceDbConfig, $destinationDbConfig, $sourceTableName, $destinationTableName) {\n    // Database connections\n    $sourceConn = new mysqli(\n        $sourceDbConfig['host'],\n        $sourceDbConfig['user'],\n        $sourceDbConfig['password'],\n        $sourceDbConfig['database']\n    );\n\n    $destinationConn = new mysqli(\n        $destinationDbConfig['host'],\n        $destinationDbConfig['user'],\n        $destinationDbConfig['password'],\n        $destinationDbConfig['database']\n    );\n\n    if ($sourceConn->connect_error || $destinationConn->connect_error) {\n        die("Connection failed: " . ($sourceConn->connect_error ?? $destinationConn->connect_error));\n    }\n\n    // Get column information for both tables\n    $query = "SHOW COLUMNS FROM `$sourceTableName`";\n    $resultSource = $sourceConn->query($query);\n    $columnsSource = [];\n    while ($row = $resultSource->fetch_assoc()) {\n        $columnsSource[$row['Field']] = $row;\n    }\n\n    $query = "SHOW COLUMNS FROM `$destinationTableName`";\n    $resultDestination = $destinationConn->query($query);\n    $columnsDestination = [];\n    while ($row = $resultDestination->fetch_assoc()) {\n        $columnsDestination[$row['Field']] = $row;\n    }\n\n    // Find columns in source that are not in destination\n    foreach ($columnsSource as $columnName => $columnInfo) {\n        if (!isset($columnsDestination[$columnName])) {\n            // Create the column with the same definition\n            $createQuery = "ALTER TABLE `$destinationTableName` ADD COLUMN `" . \n                           $columnInfo['Field'] . "` " . \n                           $columnInfo['Type'];\n            if ($columnInfo['Null'] == 'NO') {\n                $createQuery .= " NOT NULL";\n            } else {\n                $createQuery .= " NULL";\n            }\n            if (!empty($columnInfo['Default'])) {\n                $createQuery .= " DEFAULT '" . $destinationConn->real_escape_string($columnInfo['Default']) . "'";\n            }\n            if ($columnInfo['Key'] == 'PRI') {\n                $createQuery .= " PRIMARY KEY";\n            }\n\n            $destinationConn->query($createQuery);\n        }\n    }\n\n    // Close connections\n    $sourceConn->close();\n    $destinationConn->close();\n}\n\n// Example usage\n$sourceDbConfig = [\n    'host' => 'localhost',\n    'user' => 'root',\n    'password' => '',\n    'database' => 'source_db'\n];\n\n$destinationDbConfig = [\n    'host' => 'localhost',\n    'user' => 'root',\n    'password' => '',\n    'database' => 'destination_db'\n];\n\nsyncTableStructures($sourceDbConfig, $destinationDbConfig, 'tableA', 'tableB');\n\n?>