$mysqli = new mysqli("localhost", "my_user", "my_password", "world");\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n$query1 = "SELECT SQL_CALC_FOUND_ROWS Id, Name FROM my_table WHERE Name LIKE '%prashant%' LIMIT 0, 10;";\n$query2 = "SELECT FOUND_ROWS();";\n$multiQuery = $query1 . $query2;\n\nif ($mysqli->multi_query($multiQuery)) {\n    // First result set\n    if ($result = $mysqli->store_result()) {\n        while ($row = $result->fetch_assoc()) {\n            echo "Id: " . $row["Id"] . " Name: " . $row["Name"] . "\n";\n        }\n        $result->free();\n    }\n\n    // Move to the next result\n    if (!$mysqli->next_result()) {\n        die("Error: Could not move to the next result set: " . $mysqli->error);\n    }\n\n    // Second result set\n    if ($result = $mysqli->store_result()) {\n        while ($row = $result->fetch_assoc()) {\n            echo "Total Rows Found: " . $row["FOUND_ROWS()"] . "\n";\n        }\n        $result->free();\n    }\n} else {\n    die("Error executing multi-query: " . $mysqli->error);\n}\n\n$mysqli->close();