<?php\nfunction time_ago($datetime) {\n    $now = new DateTime;\n    $ago = new DateTime($datetime);\n    $diff = $now->diff($ago);\n\n    $seconds = $diff->s;\n    $minutes = $diff->i;\n    $hours = $diff->h;\n    $days = $diff->d;\n    $months = $diff->m;\n    $years = $diff->y;\n\n    if ($seconds <= 1) {\n        return "just now";\n    }\n    elseif ($minutes == 0 && $hours == 0 && $days == 0 && $months == 0) {\n        return $seconds . ' seconds ago';\n    }\n    elseif ($minutes <= 59) {\n        return $minutes . ' minute' . (($minutes == 1) ? '' : 's') . ' ago';\n    }\n    elseif ($hours <= 23 && $days == 0 && $months == 0) {\n        return $hours . ' hour' . (($hours == 1) ? '' : 's') . ' ago';\n    }\n    elseif ($days <= 6 && $months == 0 && $years == 0) {\n        return $days . ' day' . (($days == 1) ? '' : 's') . ' ago';\n    }\n    elseif ($months <= 11 && $years == 0) {\n        return $months . ' month' . (($months == 1) ? '' : 's') . ' ago';\n    }\n    else {\n        return $years . ' year' . (($years == 1) ? '' : 's') . ' ago';\n    }\n}\n\n// Example usage:\necho time_ago('2023-10-05 14:32:00');\n?>