function ExecuteQuery($sql) {\n    $stid = oci_parse($connection, $sql); // Ensure $connection is your Oracle connection resource\n    if (!$stid) {\n        $e = oci_error($connection);\n        throw new RuntimeException(htmlentities($e['message'], ENT_QUOTES), (int)$e['code']);\n    }\n\n    $r = oci_execute($stid);\n    if (!$r) {\n        $e = oci_error($stid); // Get error object from the statement\n        throw new RuntimeException(htmlentities($e['message'], ENT_QUOTES), (int)$e['code']);\n    }\n}