<?php\nfunction metersToFeetAndInches($m) {\n    // Convert meters to inches (1 meter = 39.3701 inches)\n    $inchesTotal = $m * 39.3701;\n    \n    // Calculate feet and remaining inches\n    $feet = floor($inchesTotal / 12);\n    $inches = round($inchesTotal % 12);\n    \n    return [$feet, $inches];\n}\n\n// Example usage in a loop\n$heightStartInMeters = 1; // Starting height in meters\n$heightEndInMeters = 3;   // Ending height in meters\n\nfor ($m = $heightStartInMeters; $m <= $heightEndInMeters; $m += 0.1) {\n    list($feet, $inches) = metersToFeetAndInches($m);\n    echo "Height: " . number_format($m, 2) . "m is approximately " . $feet . "' " . $inches . "\"<br>";\n}\n?>