class User extends CI_Controller {\n\n    public function __construct() {\n        parent::__construct();\n        // Load the authentication model\n        $this->load->model('authentication_model');\n    }\n\n    public function login() {\n        // Do some pre-processing and validation...\n        \n        // Use the authentication model to handle login logic\n        if ($this->authentication_model->login($username, $password)) {\n            // Authentication successful, redirect or set a session variable\n            redirect('dashboard');\n        } else {\n            // Authentication failed, show error message\n            echo "Login Failed";\n        }\n    }\n}