function simpleXSSFilter($input) {\n    $patterns = [\n        '/<script/i',\n        '/<\/script>/i',\n        '/<img/i',\n        '/on[A-Za-z]+\s*=/i',\n        '/[<>]/',  // Removes any angle brackets\n        '/alert\(/i'  // Matches alert(\n    ];\n    \n    foreach ($patterns as $pattern) {\n        if (preg_match($pattern, $input)) {\n            return htmlspecialchars($input, ENT_QUOTES);\n        }\n    }\n\n    return $input;\n}\n\n// Example usage\n$input = "<script>alert('XSS')</script>";\n$safeInput = simpleXSSFilter($input);\necho $safeInput; // Output will be &lt;script&gt;alert(&#039;XSS&#039;)&lt;/script&gt;