<?php\n\n// Simulate a database connection\n$database = [\n    'unique-slug-1' => 1,\n    'another-slug' => 2,\n    // Add more slugs and IDs as needed\n];\n\n// In-memory cache simulation (in production, use Redis or Memcached)\n$cache = [];\n\nfunction getIDFromSlug($slug) {\n    global $database, $cache;\n\n    // Check if the slug is in the cache\n    if (isset($cache[$slug])) {\n        return $cache[$slug];\n    }\n\n    // If not in cache, fetch from 'database'\n    if (isset($database[$slug])) {\n        $id = $database[$slug];\n        $cache[$slug] = $id; // Store in cache for future requests\n        return $id;\n    }\n\n    return null; // Slug not found in 'database'\n}\n\n// Example usage\n$slug = 'unique-slug-1';\n$id = getIDFromSlug($slug);\n\nif ($id) {\n    header("Location: /article.php?id=$id", true, 301);\n    exit();\n} else {\n    http_response_code(404);\n    echo "Article not found.";\n}\n\n?>