class Router {\n    public function direct($uri, $method){\n        list($controllerName, $actionName) = explode('@', $uri);\n        require_once "application/controllers/{$controllerName}.php";\n        \n        $controller = new $controllerName();\n        if (is_callable([$controller, $actionName])) {\n            call_user_func_array([$controller, $actionName], [$method]);\n        }\n    }\n}