<?php\n$servername = "localhost";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_database";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n$sql = "SHOW TABLES";\n$result = $conn->query($sql);\n\nif ($result->num_rows > 0) {\n    while($row = $result->fetch_row()) {\n        $tableName = $row[0];\n        // Change the table's collation\n        $conn->query("ALTER TABLE `$tableName` CONVERT TO CHARACTER SET utf8mb4 COLLATE utf8mb4_bin");\n        \n        // Get all columns for this specific table\n        $columnsResult = $conn->query("SHOW FULL COLUMNS FROM `$tableName` WHERE Collation != 'utf8mb4_bin'");\n        if ($columnsResult->num_rows > 0) {\n            while($columnRow = $columnsResult->fetch_assoc()) {\n                $columnName = $columnRow['Field'];\n                // Change the column's collation\n                $conn->query("ALTER TABLE `$tableName` MODIFY COLUMN `$columnName` " . $columnRow['Type'] . " CHARACTER SET utf8mb4 COLLATE utf8mb4_bin");\n            }\n        }\n    }\n} else {\n    echo "0 results";\n}\n\n$conn->close();\n?>