<?php\n$page = isset($_GET['page']) ? $_GET['page'] : 'home';\n\nfunction displayPage($content) {\n    ?>\n    <html>\n    <head><title>My Portfolio</title></head>\n    <body>\n        <?php echo $content; ?>\n    </body>\n    </html>\n    <?php\n}\n\nswitch ($page) {\n    case 'home':\n        displayPage("<h1>Welcome to My Portfolio</h1>");\n        break;\n    case 'about':\n        displayPage("<h1>About Me</h1><p>More about me.</p>");\n        break;\n    // Add more pages as needed\n    default:\n        displayPage("<h1>404 Not Found</h1>");\n}\n?>