<?php\nif(isset($_POST['city_id'])) {\n    $cityId = intval($_POST['city_id']);\n    require 'db_connection.php'; // Include your database connection file\n\n    // Retrieve areas based on city ID\n    $query = "SELECT area_id, area_name FROM tbl_area WHERE area_city = ?";\n    $stmt = $conn->prepare($query);\n    $stmt->bind_param("i", $cityId); // Bind the variable to the prepared statement as an integer\n    $stmt->execute();\n    $result = $stmt->get_result();\n\n    echo '<option value="">Choose Area</option>';\n    while ($row = $result->fetch_assoc()) {\n        echo "<option value='" . $row['area_id'] . "'>" . htmlspecialchars($row['area_name']) . "</option>";\n    }\n}\n$stmt->close();\n$conn->close();\n?>