function base_url($path = '') {\n    $base_url = isset($_SERVER['HTTPS']) && $_SERVER['HTTPS'] !== 'off' ? 'https://' : 'http://';\n    $base_url .= $_SERVER['HTTP_HOST'];\n    \n    // If your application is in a sub-directory, append it here\n    // Example: /blog/admin/\n    $sub_directory = '/admin'; // Modify as necessary\n    return $base_url . $sub_directory . ($path ? '/' . ltrim($path, '/') : '');\n}\n\n// Usage in admin/navigation.php\necho '<a href="' . base_url('../index.php') . '">Home</a>';