<?php\n\n// Assuming $db is an instance of Zend_Db_Table_Abstract\n$db = new Zend_Db_Table('your_table_name');\n\ntry {\n    // Fetching the serialized data\n    $select = $db->select()->from($db, 'serialized_column')->where('id = ?', 1);\n    $result = $db->fetchRow($select);\n\n    if ($result) {\n        $serializedData = $result['serialized_column'];\n\n        // Debug output: Check the exact string being unserialized\n        echo "Serialized data from DB: " . $serializedData . "\n";\n\n        // Unserialize the object\n        $object = unserialize(base64_decode($serializedData));\n\n        if ($object === false && !is_null($object)) {\n            throw new RuntimeException('Failed to unserialize object due to error.');\n        }\n\n        var_dump($object);\n    } else {\n        echo "No data found.";\n    }\n} catch (Exception $e) {\n    echo 'Error: ',  $e->getMessage(), "\n";\n}