<?php\n\n$to = 'jason@123.com';\n$subject = 'Daily Time Entries Update';\n$message = "Bob has approved your daily time entries.\nFor any queries, contact Bob directly at bob@123.com.";\n$headers = [\n    'From: system@abc.com',\n    'Precedence: bulk',  // Indicates that the message is unsolicited mail\n    'Auto-Submitted: auto-generated',  // Tells recipient's server not to automatically generate responses\n    'Reply-To: bob@123.com',  // Suggests where replies should go, if they are somehow made\n];\n\n// Send the email with headers\nmail($to, $subject, $message, implode("\r\n", $headers));\n\n?>