class topclass {\n    public function mode() {\n        $mode = 'functionToCall'; // Name of the method without parentheses\n\n        $class = new extendclass();\n        if (method_exists($class, $mode)) {\n            call_user_func([$class, $mode]);\n        } else {\n            echo "Method does not exist.";\n        }\n    }\n}\n\nclass extendclass extends topclass {\n    public function functionToCall() {\n        echo "Function called dynamically!";\n    }\n}\n\n// Example usage\n$top = new topclass();\n$top->mode();