// Using .=\n$start_time = microtime(true);\n$str = '';\nfor ($i = 0; $i < 100000; $i++) {\n    $str .= "test" . $i;\n}\n$end_time = microtime(true);\necho "Concatenation using .= took " . ($end_time - $start_time) . " seconds.\n";\n\n// Using array_push and implode\n$start_time = microtime(true);\n$arr = [];\nfor ($i = 0; $i < 100000; $i++) {\n    array_push($arr, "test" . $i);\n}\n$strFromArray = implode('', $arr);\n$end_time = microtime(true);\necho "Concatenation using array_push and implode took " . ($end_time - $start_time) . " seconds.\n";