function phptemplate_init() {\n  // Check if the referrer cookie does not exist already.\n  if (!isset($_COOKIE['referrer_cookie'])) {\n    // Retrieve the HTTP referer, check its validity and sanitize it.\n    $referrer = isset($_SERVER['HTTP_REFERER']) ? filter_var($_SERVER['HTTP_REFERER'], FILTER_VALIDATE_URL) : '';\n    \n    // Set the cookie with a suitable expiration time (e.g., expires in 30 days).\n    if (!empty($referrer)) {\n      setcookie('referrer_cookie', $referrer, time() + (86400 * 30), "/");\n    }\n  }\n}