<?php\n/*\nTemplate Name: Custom Archive\n*/\n\nget_header(); ?>\n\n<div id="content">\n    <h1>Archives</h1>\n\n    <?php if ( have_posts() ) : ?>\n        <?php\n        $current_month = '';\n        while ( have_posts() ) : the_post();\n            if ( get_the_date('F Y') != $current_month ) {\n                echo '<p>' . get_the_date('F Y') . '</p>';\n                echo '<ul>';\n                $current_month = get_the_date('F Y');\n            }\n            ?>\n            <li><a href="<?php the_permalink(); ?>"><?php the_title(); ?></a></li>\n        <?php endwhile; ?>\n        </ul>\n    <?php else : ?>\n        <p>No posts found</p>\n    <?php endif; ?>\n\n</div>\n\n<?php get_sidebar(); ?>\n<?php get_footer(); ?>