function stringToBinaryArray($string) {\n    $binaryArray = [];\n    for ($i = 0; $i < strlen($string); $i++) {\n        // Convert each character to its ASCII value, then to an 8-bit binary string.\n        $binaryArray[] = str_pad(decbin(ord($string[$i])), 8, '0', STR_PAD_LEFT);\n    }\n    return $binaryArray;\n}\n\n$inputString = "Hello";\n$binaryArray = stringToBinaryArray($inputString);\n\nprint_r($binaryArray);