<?php\n// Sample data\n$data = [\n    ['title' => 'Entry 1', 'description' => 'entry 1 description'],\n    ['title' => 'Entry 2', 'description' => 'entry 2 description'],\n    ['title' => 'Entry 3', 'description' => 'entry 3 description'],\n    ['title' => 'Entry 4', 'description' => 'entry 4 description'],\n    ['title' => 'Entry 5', 'description' => 'entry 5 description'],\n];\n\n// Number of columns\n$columnCount = 2;\n\n// Calculate the number of rows needed\n$rowCount = ceil(count($data) / $columnCount);\n\necho '<div style="display: flex; gap: 20px;">';\nfor ($i = 0; $i < $rowCount; $i++) {\n    echo '<div>';\n    for ($j = 0; $j < $columnCount && ($i * $columnCount + $j) < count($data); $j++) {\n        $index = $i + $j * $rowCount;\n        if (isset($data[$index])) {\n            echo '<p><strong>' . htmlspecialchars($data[$index]['title']) . '</strong></p>';\n            echo '<p>' . htmlspecialchars($data[$index]['description']) . '</p>';\n        }\n    }\n    echo '</div>';\n}\necho '</div>';\n?>