<?php\n// Database connection\n$mysqli = new mysqli("host", "user", "password", "database");\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Query to join forumcats and forums tables\n$query = "\n    SELECT f.id as forum_id, f.name as forum_name, fc.id as cat_id, fc.name as cat_name \n    FROM forums f \n    JOIN forumbats fc ON f.cat_id = fc.id \n    ORDER BY fc.id, f.id\n";\n\n$result = $mysqli->query($query);\n\nif ($result) {\n    $current_category = null;\n    \n    while ($row = $result->fetch_assoc()) {\n        if ($current_category !== $row['cat_name']) {\n            echo "<h1>{$row['cat_name']}</h1>";\n            $current_category = $row['cat_name'];\n        }\n        \n        echo "<h3>{$row['forum_name']}</h3>";\n    }\n} else {\n    echo "Error: " . $mysqli->error;\n}\n\n$mysqli->close();\n?>