function filetimeToDate($hex) {\n    // Convert the little-endian hex string to binary data\n    $binary = pack('H*', strrev($hex));\n    \n    // Unpack the binary data as an unsigned 64-bit integer\n    list(, $low, $high) = array_values(unpack("V2", $binary));\n    $int = ($high << 32) | $low;\n    \n    // Convert to Unix timestamp (subtract number of 100-nanosecond intervals between 1601 and 1970)\n    $unixTime = ($int / 1e7) - 11644473600;\n    \n    // Create a DateTime object from the Unix timestamp\n    $date = new DateTime("@$unixTime");\n    \n    return $date;\n}\n\n// Example usage with your provided value\n$filetimeHex = "60 F3 47 D1 57 98 C9 01";\n$date = filetimeToDate(str_replace(' ', '', $filetimeHex));\necho $date->format('Y-m-d H:i:s');