// Example of fetching emails using IMAP\n$imapPath = '{imap.example.com:993/imap/ssl}INBOX';\n$username = 'your_username@example.com';\n$password = 'your_password';\n\n$mbox = imap_open($imapPath, $username, $password);\n$emails = imap_search($mbox, 'ALL');\n\nif ($emails) {\n    rsort($emails);\n    foreach ($emails as $email_number) {\n        $overview = imap_fetch_overview($mbox, $email_number, 0)[0];\n        echo "Subject: {$overview->subject}\n";\n        // Fetch body and handle attachments securely\n    }\n}\nimap_close($mbox);