class DatabaseConnection {\n    private $pdo;\n\n    public function __construct($dsn, $username = '', $password = '') {\n        $this->pdo = new PDO($dsn, $username, $password);\n    }\n\n    public function getConnection() {\n        return $this->pdo;\n    }\n}\n\nfunction getSomething(DatabaseConnection $db) {\n    $conn = $db->getConnection();\n    // Use the connection\n}\n\n// Usage\n$db = new DatabaseConnection('your_dsn_here', 'username', 'password');\ngetSomething($db);