$url = 'http://www.example.com/page.html';\n$ch = curl_init($url);\n\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\ncurl_setopt($ch, CURLOPT_TIMEOUT, 10); // Set the timeout in seconds\n$response = curl_exec($ch);\n$httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);\n$errorNumber = curl_errno($ch);\n$errorMessage = curl_error($ch);\n\nif ($errorNumber) {\n    if ($errorNumber == CURLE_OPERATION_TIMEDOUT || $errorNumber == CURLE_CONNECTION_FAILED) {\n        // Handle timeout or connection failure\n        echo "Request timed out or failed to connect.\n";\n        // You can retry here if needed\n    } else {\n        // Other errors (e.g., DNS resolution error)\n        echo "cURL error ($errorNumber): $errorMessage\n";\n    }\n} elseif ($httpCode == 404) {\n    // Handle 404 Not Found\n    echo "Page not found (HTTP 404).\n";\n    // Add to list of not found pages here\n} else {\n    // Success or other codes\n    if ($response !== false && $httpCode >= 200 && $httpCode < 300) {\n        $page = simplexml_load_string($response);\n        // Proceed with processing the page\n    } else {\n        echo "HTTP request failed with code: $httpCode\n";\n    }\n}\n\ncurl_close($ch);