class Router {\n    private $request;\n    const DEFAULT_SHOP_CONTROLLER = 'store';\n    \n    public function __construct(DPLS_Request $request) {\n        $this->request = $request;\n    }\n    \n    public function routeRequest() {\n        // Check if a subdomain exists and set the appropriate controller\n        if ($this->request->_segment && empty($this->request->_request['controller'])) {\n            $this->request->_request['controller'] = self::DEFAULT_SHOP_CONTROLLER;\n        }\n        \n        // Set default action if none is provided\n        if (empty($this->request->_request['action'])) {\n            $this->request->_request['action'] = 'defaultAction';\n        }\n        \n        // Proceed with routing the request to the appropriate controller and action\n        $controllerName = ucfirst($this->request->_request['controller']) . 'Controller';\n        $actionName = $this->request->_request['action'];\n        \n        $controller = new $controllerName();\n        if (method_exists($controller, $actionName)) {\n            call_user_func_array([$controller, $actionName], []);\n        } else {\n            // Handle 404\n            header("HTTP/1.0 404 Not Found");\n        }\n    }\n}