<?php\n   // URL of the API endpoint\n   $url = "http://www.trynt.com/symbol-lookup-api/v2/?s=IBM&fo=json&f=0";\n   \n   // Setting context options for allowing redirects and specifying timeout\n   $contextOptions = [\n       'http' => [\n           'method'  => 'GET',\n           'header'  => "User-Agent: MyCustomUserAgent\r\n", // some servers require a user agent\n           'timeout' => 5, // seconds\n       ],\n   ];\n   \n   $context = stream_context_create($contextOptions);\n   \n   try {\n       // Fetch the JSON data from the API\n       $jsonString = file_get_contents($url, false, $context);\n       \n       if ($jsonString === FALSE) {\n           throw new Exception("Failed to fetch content.");\n       }\n       \n       // Decode the JSON string into a PHP array/object\n       $jsonData = json_decode($jsonString, true);\n       \n       // Output the JSON data (or process it as needed)\n       header('Content-Type: application/json');\n       echo json_encode($jsonData);\n   } catch (Exception $e) {\n       echo "Error: ",  $e->getMessage();\n   }\n   ?>