<?php\n$descriptorspec = array(\n   0 => array("pipe", "r"),  // stdin is a pipe that the child will read from\n   1 => array("pipe", "w"),  // stdout is a pipe that the child will write to\n   2 => array("file", "/tmp/error-output.txt", "a") // stderr is a file to write to\n);\n\n$process = proc_open('./RUNMYSCRIPT.sh', $descriptorspec, $pipes);\n\nif (is_resource($process)) {\n    // Send input to the process after sleeping for a few seconds.\n    sleep(10);\n    fwrite($pipes[0], "q\n");\n    fclose($pipes[0]);  // Close stdin as we've sent all data.\n\n    // Read output from the script if necessary\n    $output = stream_get_contents($pipes[1]);\n    fclose($pipes[1]);\n\n    proc_close($process);\n\n    echo "Script output: " . $output;\n}\n?>