function processContent($content) {\n    // Regex pattern to find placeholders for scripts\n    $pattern = '/\[\[INCLUDE_SCRIPT:(.*?)\]\]/';\n    preg_match_all($pattern, $content, $matches);\n    \n    // Replace placeholders with empty strings or markers if needed\n    $processed_content = preg_replace($pattern, '', $content);\n    \n    // Return the list of scripts that need to be included\n    return ['content' => $processed_content, 'scripts' => $matches[1]];\n}\n\n// Example usage\n$page_content = '<html>...[[INCLUDE_SCRIPT:/path/to/script.php]]...</html>';\n$result = processContent($page_content);\n\n// Output the processed content\necho $result['content'];\n\n// Include scripts from the result\nforeach ($result['scripts'] as $script_path) {\n    // Ensure the script path is safe and valid before including\n    if (file_exists(realpath($script_path)) && !preg_match('/\.\.[\/\\\\]/', $script_path)) {\n        include realpath($script_path);\n    } else {\n        error_log("Script not found or invalid: $script_path");\n    }\n}