<?php\nob_start(); // Start output buffering to prevent headers from being sent prematurely\n\n// Attempt to start session only if not already started\nif (session_status() == PHP_SESSION_NONE) {\n    session_start();\n}\n\n$response = array(\n    'status' => 'abc',\n    'a' => 'abc',\n    'b' => 'abc',\n    'c' => 'abc'\n);\n\nheader('Content-Type: application/json'); // Setting correct content type for JSON response\necho json_encode($response);\nob_end_flush(); // Flush output buffer\nexit;