function generateUniqueFolderName($length = 10) {\n    // Ensure the length is within desired limits\n    $length = max(5, min(15, (int)$length));\n    \n    // Generate random bytes and convert them to a hexadecimal string\n    $bytes = random_bytes(ceil($length / 2));\n    $folderName = substr(bin2hex($bytes), 0, $length);\n    \n    return $folderName;\n}\n\n// Example usage\necho generateUniqueFolderName();